package corejava;

public class Assignmentoperaters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =30;
		a+=5;
		System.out.println(a);
		a-=5;
		System.out.println(a);
		a*=5;
		System.out.println(a);
		a/=5;
		System.out.println(a);
		a%=5;
		System.out.println(a);		
		
		
		
		
		
		
		
		
		
		
		
		
	}
}
