package corejava;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			int a=10;  
			int b=5;  
			System.out.println(a++);//10  11
			System.out.println(b++);//  5   6
			System.out.println(++a);//  11  12
			System.out.println(++b);//  6   7
			
	}

}
